Scripts.OnClick:
if(self:GetChecked()) then
	crhCp = true

	CatRotationHelperHeader:RegisterEvent("UNIT_COMBO_POINTS");
	CatRotationHelperSetCatCP(GetComboPoints("player"));
else
	CatRotationHelperSetCatCP(0);
	CatRotationHelperHeader:UnregisterEvent("UNIT_COMBO_POINTS");

	crhCp = false
end

Scripts.OnHide:
if ( self.animIn:IsPlaying() ) then
    self.animIn:Stop();
end
if ( self.animOut:IsPlaying() ) then
    self.animOut:Stop();
end

Scripts.OnPlay:
                        local frame = self:GetParent();
                        local frameWidth, frameHeight = frame:GetSize();
						frame.animOut:Stop()
                        frame.innerGlow:SetSize(frameWidth * 0.7, frameHeight * 0.7);
                        frame.innerGlow:SetAlpha(0.6);
                        frame.outerGlow:SetSize(frameWidth * 1.25, frameHeight * 1.25);
                        frame.outerGlow:SetAlpha(0.6);
                        frame.icon:SetSize(frameWidth * 1.05, frameHeight * 1.05);
                        frame.icon:SetAlpha(0.7);
                        frame:Show();

                        local frame = self:GetParent();
                        local frameWidth, frameHeight = frame:GetSize();
						frame.animIn:Stop()
                        frame.innerGlow:SetAlpha(0.0);
                        frame.outerGlow:SetSize(frameWidth * 0.95, frameHeight * 0.95);
						frame.outerGlow:SetAlpha(0.6);
                        frame.icon:SetSize(frameWidth * 0.9, frameHeight * 0.9);
                        frame.icon:SetAlpha(0.7);
                        frame:Show();
